/* PackingDetailsMapper.getSearch */ SELECT * FROM( SELECT A.XRT_INVC_SNO , A.SHIPMENT_BL_NO , A.ORD_CNT , ISNULL(A.INVC_SNO1, '') AS INVC_SNO , A.WGT , (SELECT TOP 1 STUFF( (SELECT ', '+ od.GOODS_NM + CASE WHEN od.GOODS_OPTION IS NULL THEN '' WHEN od.GOODS_OPTION = '' THEN '' ELSE '/' + od.GOODS_OPTION END GOODS_OPTION FROM TORDERDTL od WHERE 1 = 1 AND A.ORD_CD = od.ORD_CD FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 1, '') FROM TORDERDTL) AS GOODS , (SELECT P02.ENG_CEO FROM P002 P02 WHERE A.ORGCD = P02.ORGCD) AS SELLER_CEO , (SELECT P02.NAME FROM P002 P02 WHERE A.ORGCD = P02.ORGCD) AS SELLER_NAME , (SELECT P02.ENG_ADDR FROM P002 P02 WHERE A.ORGCD = P02.ORGCD) AS SELLER_ADDR , (SELECT P02.TEL1 FROM P002 P02 WHERE A.ORGCD = P02.ORGCD) AS SELLER_TEL , A.RECV_NAME , A.RECV_ADDR1 , A.RECV_ADDR2 , A.RECV_CITY , A.RECV_TEL , A.RECV_STATE , A.RECV_POST , 'WEST' AS ZONE , A.SHIPPING_COMPANY , (SELECT CODEKEY FROM S002 WHERE CODE = A.RECV_STATE AND CODEKEY IN('US_STATE_EAST', 'US_STATE_WEST')) AS stateGubun FROM TORDER A WHERE 1 = 1 AND A.E_NATION = #{country}
AND A.SHIP_METHOD_CD = 'PREMIUM'
AND A.SHIP_METHOD_CD = 'DHL'
AND A.SHIP_METHOD_CD = 'PREMIUM'
AND A.DEL_FLG = 'N' AND A.STATUS_CD BETWEEN '30' AND '60' AND A.STOCK_DATE BETWEEN CONVERT(VARCHAR(10), CONVERT(date, CONVERT(VARCHAR(8), #{DATE_TO}))) + ' 00:00:00' AND CONVERT(VARCHAR(10), CONVERT(date, CONVERT(VARCHAR(8), #{DATE_FROM}))) + ' 23:59:59'
AND A.SHIPMENT_BL_NO IN
#{item.shipmentBlNos}
) B WHERE 1 = 1
AND B.stateGubun = #{usState}
ORDER BY XRT_INVC_SNO, SHIPMENT_BL_NO ASC

/* PackingDetailsMapper.getSearch */ SELECT SHIPMENT_BL_NO AS shipmentBlNo, COUNTRY AS country, CASE WHEN CUSTOMS_CLEARANCE = 'G' THEN '일반' WHEN CUSTOMS_CLEARANCE = 'L' THEN '목록' ELSE '' END AS customsClearanceNm, CONVERT(CHAR(19), ADDDATETIME, 20) AS adddatetime FROM SHIPMENT_BL WITH (NOLOCK)
WHERE SHIPMENT_BL_NO IN( SELECT SHIPMENT_BL_NO FROM TORDER WHERE SHIP_METHOD_CD = 'PREMIUM' )
WHERE SHIPMENT_BL_NO IN( SELECT SHIPMENT_BL_NO FROM TORDER WHERE SHIP_METHOD_CD = 'DHL' )
WHERE SHIPMENT_BL_NO IN( SELECT SHIPMENT_BL_NO FROM TORDER WHERE SHIP_METHOD_CD = 'PREMIUM' )
AND SHIPMENT_BL_NO = #{shipmentBlNo}
AND COUNTRY = #{country}
AND CLOSE_YN = 'Y' AND ADDDATETIME BETWEEN #{DATE_TO} + ' 00:00:00' AND #{DATE_FROM} + ' 23:59:59' ORDER BY ADDDATETIME ASC
